import React, { Component } from 'react';
import axios from 'axios';
import {
    Card,
    Table,
    Image,
    ButtonGroup,
    Button,
    InputGroup,
    FormControl,
  } from "react-bootstrap";


export default class Books extends Component {

    constructor(props){
        super(props);
        this.state = {
            books: []
        }
    }

    componentDidMount(){
        axios.get('http://localhost:8080/api/books/show-all')
            .then(response => response.data)
            .then((data) => {
                this.setState({books: data})
            })
    }

    render() {
      return (
        <Card className={"border border-dark bg-dark text-white"}>
        <Card.Header>
        <div style={{ float: "left" }}>
            </div>
            <div style={{ float: "right" }}>
            <InputGroup size="sm">
                <FormControl
                  placeholder="Search"
                  name="search"
                  value={search}
                  className={"info-border bg-dark text-white"}
                  onChange={this.searchChange}
                />
                <InputGroup.Append>
                  <Button
                    size="sm"
                    variant="outline-info"
                    type="button"
                    onClick={this.searchData}
                  >
                  </Button>
                  <Button
                    size="sm"
                    variant="outline-danger"
                    type="button"
                    onClick={this.cancelSearch}
                  >
                  </Button>
                </InputGroup.Append>
              </InputGroup>
            </div>
        </Card.Header>
        <Card.Body>
          <Table bordered hover striped variant="dark">
          <table className="border border-dark bg-dark text-white">
                <thead>
                    <tr>
                        <th>№</th>
                        <th>Автор</th>
                        <th>Название</th>
                    </tr>
                </thead>
                <tbody>
                {
                    this.state.books.map(
                                book => 
                                <tr key = {book.id}>
                                     <td> {this.state.books.id}</td>   
                                     <td> {book.authorName}</td>   
                                     <td> {book.bookName}</td>   
                                </tr>
                            )
                }
                </tbody>
            </table>
          </Table>
        </Card.Body>
        <Card.Footer>
        </Card.Footer>
      </Card>
      )
    }
  }
