import React, { Component } from 'react';
import axios from 'axios';

const BOOKS_REST_API_URL = 'http://localhost:3000/api/books/show-all';

export default class Books extends Component {

    constructor(props){
        super(props);
        this.state = {
            books: []
        }
    }

    componentDidMount(){
        axios.get(BOOKS_REST_API_URL)
            .then(response => response.data)
            .then((data) => {
                this.setState({books: data})
            })
    }

    render() {
      return (
        <div>
            <table className="border border-dark bg-dark text-white">
                <thead>
                    <tr>
                        <th>№</th>
                        <th>Автор</th>
                        <th>Название</th>
                    </tr>
                </thead>
                <tbody>
                {
                    this.state.books.map(
                                book => 
                                <tr key = {book.id}>
                                     <td> {this.state.books.length}</td>   
                                     <td> {book.authorName}</td>   
                                     <td> {book.bookName}</td>   
                                </tr>
                            )
                }
                </tbody>
            </table>
        </div>
      )
    }
  }
