import React, { Component } from 'react';
import axios from 'axios';
import {Card, Table, Image} from 'react-bootstrap'


export default class Books extends Component {

    constructor(props){
        super(props);
        this.state = {
            books: []
        }
    }

    componentDidMount(){
        axios.get('http://localhost:8080/api/books/show-all')
            .then(response => response.data)
            .then((data) => {
                this.setState({books: data})
            })
    }

    render() {
      return (

        <Card className={"bordered border-dark bg-dark"} style={{marginTop: 50, marginBottom: 50, marginLeft: 50, marginRight: 50}}>
            <Card.Header>
                Booklist
            </Card.Header>
            <Card.Body>
                <Table striped bordered hover variant="dark">
                <thead>
                    <tr>
                        <th>№</th>
                        <th>Автор</th>
                        <th>Название</th>
                     </tr>
                 </thead>
                 <tbody>
                {
                     this.state.books.map(
                                 book => 
                                 <tr key = {book.id}>
                                  <td>
                                      {book.id}</td>      
                                  <td> <Image src={book.coverPicURL} roundedCircle width="30" hight="30"/> {book.bookName}</td>
                                  <td> {book.authorName}</td>   
                                 </tr>
                             )
                 }
                 </tbody>
                </Table>
            </Card.Body>
            <Card.Footer></Card.Footer>
        </Card>
      )
    }
  }
